template:
(function() {
    const debug = true;
    function log(message) {
        if(debug) {
            tableau.log(message);
        }
    }
    // helper functions
    function getColUrlFromInput(connectionData) {
        var urlArray = connectionData.split(",");
        // append Node Red server host and port to url
        var url = "http://" + window.location.hostname + ":" + window.location.port + urlArray[0];
        log(url);
        return url;
    }

    function getDataUrlFromInput(connectionData) {
        var urlArray = connectionData.split(",");
        // append Node Red server host and port to url
        var url = "http://" + window.location.hostname + ":" + window.location.port + urlArray[1];
        log(url);
        return url;
    }
    // Create the connector object
    var myConnector = tableau.makeConnector();

    // Define the schema
    myConnector.getSchema = function(schemaCallback) {
        var columnsUrl = getColUrlFromInput(tableau.connectionData);
        var tableSchema;
         $.ajax(
             {
                "url": columnsUrl, 
                "dataType": "json",
                "success": function(resp) {
                    log(resp);

                    tableSchema = {
                        id: "getCompassQuery",
                        alias: "Compass Query Data",
                        columns: resp
                    }

                    schemaCallback([tableSchema]);


                },
                "error": function(xhr, ajaxOptions, thrownError) {
                    tableau.abortWithError("An error occurred. Request returned status code: " + xhr.status + "\nError Message received was: " + thrownError);
                }
            });
    }

    // Download the data
    myConnector.getData = function(table, doneCallback) {
        var getDataUrl = getDataUrlFromInput(tableau.connectionData);
        $.ajax(
            {
                "url": getDataUrl, 
                "dataType": "json",
                "success": function(resp) {
                    var result = resp,
                        tableData = [];
                    log(result);
                    // Iterate over the JSON object
                    for (var i = 0; i < result.length; i++) {
                        log(result[i]);
                        tableData.push(result[i]);
                    }
                    table.appendRows(tableData);
                    log(tableData);

                    doneCallback();
                },
                "error": function(xhr, ajaxOptions, thrownError) {
                    tableau.abortWithError("An error occurred. Request returned status code: " + xhr.status + "\nError Message received was: " + thrownError);
                }
        });
    };

    setupConnector = function() {
        var host = $('#node-input-host').val();
        var port = $('#node-input-port').val();
        var username = $('#node-input-username').val();
        var password = $('#node-input-password').val() || "";
        var query = $('#node-input-queryList').val();
        var folder = $('#node-input-folder').val();
        var app = $('#node-input-appList').val();
        var appArray = app.split('-');
        var repo = appArray[0];
        var db = appArray[1];
        var error = $('#node-input-error').val();


        if(error !== "empty") {
            tableau.abortWithError(error);
        }        

        // check to see if query name or root folder has spaces so it can be formatted correctly for url
        var queryName = query.split(' ').join('%20');
        var rootFolder = folder.split(' ').join('%20');

        colUrls = "/compassData?username=" + username + "&password=" + password + "&repo=" + repo + "&db=" + db + "&queryPath=" + rootFolder + '/' + queryName + "&queryDef=true" + "&host=" + host + "&port=" + port;
        dataUrls = "/compassData?username=" + username + "&password=" + password + "&repo=" + repo + "&db=" + db + "&queryPath=" + rootFolder + '/' + queryName + "&host=" + host + "&port=" + port;

        tableau.connectionData = colUrls + "," + dataUrls;
        tableau.connectionName = "Compass Query Data"; // This will be the data source name in Tableau
        tableau.submit(); // This sends the connector object to Tableau
    };

    tableau.registerConnector(myConnector);

    // Create event listeners for when the user submits the form
    $(document).ready(function() {
        $("#submitButton").click(function() {
            setupConnector();
        });
    });
})();
